<table><tr><th>First Name</th><th>Last Name</th><th>Team Name</th><th>Email Address</th><th>Phone</th><th>Address</th><th>Winning Wallet</th><th>Deposit Wallet</th><th>Bonus Wallet</th><th>Current Wallet</th><th>Created</th></tr> <?php foreach ($records as $row) { ?><tr><td><?php echo ucfirst($row->firstname); ?></td><td><?php echo ucfirst($row->lastname); ?></td><td><?php echo ucfirst($row->team_name); ?></td><td><?php echo $row->email; ?></td><td><?php echo $row->country_mobile_code ? $row->country_mobile_code.'-': "";echo $row->phone ? $row->phone : ""; ?></td><td><?php echo $row->addressline1 ? $row->addressline1.',': "";echo $row->addressline2 ? $row->addressline2.',': "";echo $row->city ? $row->city.'': ""; echo $row->stateName ? $row->stateName.',': ""; echo $row->countryName ? $row->countryName.'': ""; echo $row->pincode ? $row->pincode : ""; ?></td><td><?php echo $row->winning_wallet;?></td><td><?php echo $row->deposit_wallet;?></td><td><?php echo $row->bonus_wallet; ?></td><td><?php echo ( $row->winning_wallet + $row->deposit_wallet + $row->bonus_wallet ); ?></td><td><?php echo $row->created ? date(DATE_TIME_FORMAT_ADMIN, ($row->created)) : ""; ?></td></tr> <?php } ?></table>