<table><thead><tr><th>First Name</th><th>Last Name</th><th>Team Name</th><th>Email/Phone</th><th>Played series</th><th>Played Match</th><th>Played Contest</th><th>Credited Amount</th><th>Debited Amount</th><th>Refund Amount</th><th>Earnings</th><th>Tax Amount</th></tr></thead><tbody> <?php foreach ($records as $row) { ?><tr><td> <?php echo ucfirst($row->firstname); ?></td><td> <?php echo ucfirst($row->lastname); ?></td><td> <?php echo ucfirst($row->team_name); ?></td><td> <?php echo $row->email.","; echo $row->country_mobile_code ? $row->country_mobile_code.'-': ""; echo $row->phone ? $row->phone : ""; ?></td><td> <?php echo $row->played_series_counts; ?></td><td><?php echo $row->played_match_counts; ?></td><td> <?php echo $row->customer_contests; ?></td><td> <?php echo number_format( $row->spendamount, 2 ); ?></td><td> <?php echo number_format( $row->winamount, 2); ?></td><td> <?php echo number_format( $row->refund_amount, 2); ?></td><td> <?php echo number_format( $row->earnings, 2); ?></td><td> <?php echo number_format($row->tax_amount, 2); ?></td></tr> <?php } ?></tbody></table>