<table><thead><tr><th>Unique id</th><th>Match Name</th><th>Series Name</th><th>Game type</th><th>Team-1</th><th>Team-2</th><th>Contest counts</th><th>Joined Teams</th><th>Credited Amount</th><th>Debited Amount</th><th>Refund Amount</th><th>Earnings</th><th>Tax Amount</th><th>Match date</th><th>Close date</th></tr></thead><tbody> <?php foreach ($records as $row) { ?><tr><td> <?php echo ucfirst($row->unique_id); ?></td><td> <?php echo ucfirst($row->name); ?></td><td> <?php echo ucfirst($row->series_name); ?></td><td> <?php echo ucfirst($row->game_type_name); ?></td><td> <?php echo ucfirst($row->team_1_name); ?></td><td> <?php echo ucfirst($row->team_2_name); ?></td><td> <?php echo ucfirst($row->contest_count); ?></td><td> <?php echo ucfirst($row->joined_total_teams); ?></td><td> <?php echo number_format($row->spendamount,2); ?></td><td> <?php echo number_format($row->winamount,2); ?></td><td> <?php echo number_format($row->refund_amount,2); ?></td><td> <?php echo number_format($row->earnings,2);?></td><td> <?php echo number_format($row->tax_amount,2); ?></td><td> <?php echo date(DATE_TIME_FORMAT_ADMIN,$row->match_date); ?></td><td> <?php echo date(DATE_TIME_FORMAT_ADMIN,$row->close_date); ?></td></tr> <?php } ?></tbody></table>